void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x=(uv.x-.5);
    uv.x *=iResolution.x/iResolution.y;

    float persp = uv.y+3.;
    vec2 gv = (fract(uv*vec2(persp,persp))-vec2(.5,.5));
    float mEdges = smoothstep(.4,.5,gv.x)+smoothstep(.4,.5,gv.y);
    float diag1 = smoothstep(-.05,0.,(gv.y-gv.x)*(gv.x+gv.y)*10.);
    float diag2 = smoothstep(-.05,0.,(gv.x-gv.y)*(gv.y+gv.x)*10.);
    float faceN = max(diag1*sign(gv.y),0.);
    float faceE = max(diag2*sign(gv.x),0.);
    float faceS = max(diag1*sign(-gv.y),0.);
    float faceW = max(diag2*sign(-gv.x),0.);
    vec3 n = vec3(faceW-faceE,faceS-faceN,4.);
    n = n/length(n);
    vec3 sun = vec3(1.,.5,-1.);
    float angle = iTime;
    mat3 rot = mat3(cos(angle),sin(angle),0.,
                    -sin(angle),cos(angle),0.,
                    0.,0.,1.);
    sun = rot*sun;
    sun = sun/length(sun);
    float light = -dot(n,sun);
    float angle2 = iTime +uv.x*uv.y;
    mat3 rot2 = mat3(cos(angle2),sin(angle2),0.,
                    sin(angle2),-cos(angle2),0.,
                    0.,0.,1.);
    vec3 col = vec3(.8,.4,.6)*rot2;
    vec3 toDisplay = (col+1.)*.5*light;
    fragColor = vec4(toDisplay,1.0);
}
