// adapted from FabriceNeyret2's https://www.shadertoy.com/view/wllcRN
// inspired from python273 https://shadertoy.com/view/4ldyW8
// See also https://www.shadertoy.com/view/ttscRN

void mainImage( out vec4 O, vec2 U )
{
    ivec2 I = ivec2(U + 32.0 * iTime) & 65535;
    int x = I.x, y = I.y,
        i = (x - y ^ y + x);
    float f = fract(float(i) * 0.819);
    float v  = (floor(mod(iTime, 256.0) + 10.0) * 0.1875) + f;
    float v1 = (floor(mod(iTime, 256.0) + 11.0) * 0.1875) + f;
    O = vec4(mix(fract(v * v * v), fract(v1 * v1 * v1), fract(iTime)) * 3.0 - 1.0);
}
