float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float arx = min(1.0, iResolution.y / iResolution.x);
  float ary = min(1.0, iResolution.x / iResolution.y);

  vec2 uv;

  vec2 fc = fragCoord / iResolution.xy * 2.0 - 1.0;
  vec2 armin = iResolution.xy / min(iResolution.x, iResolution.y);
  vec2 xy = fc * armin;
  float d = length(xy);
  if (d < 1.0) {
    float z = sqrt(1.0 - d * d);
    float r = atan(d, z) / PI;
    float phi = atan(xy.y, xy.x);

    uv.x = (r * cos(phi) + 0.5) * arx + (1.0 - arx) / 2.0;
    uv.y = (r * sin(phi) + 0.5) * ary + (1.0 - ary) / 2.0;

    vec4 c = texture(iChannel0, uv);
  	fragColor = c;
  } else {
	fragColor = vec4(0.0, 0.0, 0.0, 1.0);
  }

}
