vec4 moveRight(vec2 xy,float size)
{
    float l=step(size,xy.x);
    vec3 black=vec3(0.);
    xy.x-=size;
    vec4 texColor=l*texture(iChannel0,xy)+(1.-l)*vec4(black,1.);
    return texColor;
}
vec4 moveLeft(vec2 xy,float size)
{
    float l=step(xy.x,1.-size);
    vec3 black=vec3(0.);
    xy.x+=size;
    vec4 texColor=l*texture(iChannel0,xy)+(1.-l)*vec4(black,1.);
    return texColor;
}
vec4 moveUp(vec2 xy,float size)
{
    float l=step(size,xy.y);
    vec3 black=vec3(0.);
    xy.y-=size;
    vec4 texColor=l*texture(iChannel0,xy)+(1.-l)*vec4(black,1.);
    return texColor;
}
vec4 moveDown(vec2 xy,float size)
{
    float l=step(xy.y,1.-size);
    vec3 black=vec3(0.);
    xy.y+=size;
    vec4 texColor=l*texture(iChannel0,xy)+(1.-l)*vec4(black,1.);
    return texColor;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy=fragCoord/iResolution.xy;
  // vec4 texColor=moveRight(xy,0.1);
  // vec4 texColor=moveLeft(xy,0.1);
  // vec4 texColor=moveUp(xy,0.1);
     vec4 texColor=moveDown(xy,0.1);
     fragColor=texColor;


}
