void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 p = vec2(fragCoord.xy / iResolution.xy);

    // 背景カラー
    vec3 col = texture(iChannel0, p).xyz;

    vec3 pixel = vec3(0.0f);
    if(col.x + col.y + col.z > 1.5f)
        pixel = vec3(1.0f);

    bool flag = false;
    if(col.x < 0.4f && col.z < 0.4f && col.y > 0.4f)
        flag=true;

    if(flag)
        pixel = mix(pixel, texture(iChannel0, p).xyz, 0.0f);
    else
        pixel = mix(pixel, texture(iChannel0, p).xyz, texture(iChannel1, p).xyz);

    fragColor = vec4(pixel.x + mod(iTime,0.02f),pixel.y * mod(iTime,0.8001f),pixel.z - mod(iTime,0.1f), 1.0f);
}
