// trying to recreate this https://codepen.io/gskinner/pen/mmbpjv

float circle_d(vec2 uv)
{
    return length(uv);
}

float circle_m(vec2 uv)
{
    return smoothstep(1., 0.95, circle_d(uv));
}

vec2 trans(vec2 uv, vec2 xy, float scale)
{
    uv -= xy;
    uv /= scale;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5)/(iResolution.xy - 1.);
    float asr = iResolution.x/iResolution.y; // aspect ratio
    uv.x *= asr;
    vec2 temp_uv = vec2(uv.x - iTime, uv.y);
    vec2 id = floor(temp_uv * 8.);
    vec2 old_uv = uv;
    uv.x -= iTime;
    //uv.y += id.x * 0.05;
    uv.y += 2. * sin(id.x * 0.05);
    uv = fract(uv * 8.);
    vec3 col = vec3(uv, 0.);
    //vec3 col = vec3(id * 0.1, 0.);

    //vec3 col = vec3(0.);
    vec2 circle_pos = vec2(.5, .5);
    float circle_size = .1;

    float mask = circle_m(trans(uv, circle_pos, circle_size));
    col = mix(col, vec3(1.), mask);

    // Output to screen
    fragColor = vec4(col,1.0);
}
