    vec2 uv = (fragCoord*2.+iResolution.xy)/iResolution.y;

    uv = uv*cos(log(iTime -(uv.x/2.)));
    uv=abs(uv);
   uv*=20.5;


    vec3 col1 = vec3(105, 60, 236)/256.;
    vec3 col2 = vec3(255, 100, 199)/256.;

    vec3 col = mix(col1, col2, fract(uv.x));

 	fragColor = vec4(col, 1.);
}
