void Grid(vec2 UV, vec2 LineSize,vec2 GridSize,out float Out)
{
    vec2 p= fract(UV*LineSize+GridSize/2.0);
    Out= step(GridSize.x, p.x)*step(GridSize.y, p.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 st =  ( 2.*fragCoord - iResolution.xy ) / iResolution.y;
    vec2 st= (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    float c;
    Grid(st,vec2(6.0),vec2(0.1),c);
	fragColor = vec4(vec3(c),1.0);
}
