// I need to have 10 random points, which move around predicatably, 
// which can be calculated repeatedly, using nothing but iTime

// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float min_dist = max(iResolution.x, iResolution.y); // Technically not, but basically max distance
	// Let the N points be (noise(n, iTime), noise(iTime, n))
    for (float n = 0.0; n < 10.0; n++) {
        vec2 point = vec2(noise(vec2(n, sin(iTime))),
                          noise(vec2(cos(iTime), n)));
        vec2 diff = uv - point;
        min_dist = min(length(diff), min_dist);
    }

    min_dist = 1.0-min_dist;
    min_dist = min_dist * min_dist;
    vec3 col = vec3(min_dist, min_dist, min_dist);


    // Output to screen
    fragColor = vec4(col,1.0);
}
