void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col = texture(iChannel0,uv).rgb;
    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 R(float a){ 
    return mat2 (cos(a),-sin(a)
        ,sin(a),cos(a));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord/iResolution.xy;
    vec4 old = texture(iChannel1,uv);
    vec2 st1 = uv - 0.5;
    //st1 *=R(0.1*iTime);
	st1+=0.5;
    vec4 new1 = texture(iChannel0,st1);
	vec4 mix1 = mix(old,new1,0.1);
    //mix1.r*= step(0.1,fract(st1.y*10.));
    vec2 st2= uv - 0.5;
    st2 *=R(-0.1*iTime);
	st2+=0.5;
    vec4 new2 = texture(iChannel0,st2);
    //mix1.g*= step(0.1,fract(st2.y*10.));

	vec4 mix2 = mix(old,new2,0.1);
	fragColor = max(mix1,mix2);
    fragColor = mix1;
}
