#define pi 3.1415

//Modify this to increase the radius of the spinners
#define rad 0.1 

//Modify this to increase the weight of the spinners
#define wdt 2.0 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Modify the subtracted vec2 to move the spinners
    vec2 uv = fragCoord / iResolution.xy - vec2(0.08, 1.0 - 0.14); 
    uv.x *= iResolution.x / iResolution.y;
    float pxscale = 1.0 / iResolution.y;

    //Outer spinner rotation
    vec2 uv0 = vec2(uv.x * cos(iTime * 3.5) + uv.y * -sin(iTime * 3.5), uv.x * sin(iTime * 3.5) + uv.y * cos(iTime * 3.5));
    float polar0 = atan(uv0.x, uv0.y) + pi;
    float uvlen0 = length(uv0);

    //Inner spinner rotation
    vec2 uv1 = vec2(uv.x * cos(iTime * 3.5) + uv.y * sin(iTime * 3.5), uv.x * -sin(iTime * 3.5) + uv.y * cos(iTime * 3.5));
    float polar1 = atan(uv1.x, uv1.y) + pi;
    float uvlen1 = length(uv1);

    vec4 col = vec4(0.0);

    //Outer spinner behavior
    if(polar0 <= (sin(iTime * 1.2) + 1.0) * pi)
    {
        col += vec4(smoothstep(pxscale * 3.0, 0.0, abs(rad - uvlen0) - wdt * pxscale));
    }

    //Inner spinner behavior
    if(polar1 >= (cos(iTime * 1.2) + 1.0) * pi)
    {
        col += vec4(smoothstep(pxscale * 3.0, 0.0, abs(rad - 12.0 /  iResolution.y - uvlen1) - wdt * pxscale));
    }

    //Modify the vec4 to change the color of the spinners
    col *= vec4(0.012, 0.4, 0.839, 1.0); 

    //Modify the vec4 to change the color of the background
    col += vec4(0.141, 0.161, 0.18, 1.0) * (1.0 - col.w); 

    fragColor = col;
}
