# define time iTime * .02 + 1000.

void mainImage (out vec4 _, in vec2 uv)
{
    vec2 s = (2.*uv - iResolution.xy)/min(iResolution.x,iResolution.y);

    vec3 b = texture(iChannel0,.5*s+vec2(.01*time,.2*time+.5)).rrr;

    float z = 70.*time;
    s += .25*vec2(sin(z),cos(z));
    s += 4.*vec2(sin(time),cos(time));

    float l = length(s);
    float a = acos(dot(vec2(cos(time),sin(time)),s/l))/3.14159;

    l = smoothstep(2.,1.,1.+.5*tan(10.*sin(time*.4)*a))*l;

    vec3 n = normalize(vec3(exp(-a*a),exp(-l*l),.2));

    vec3 L = normalize(vec3(cos(.5*time),sin(.5*time),1));

    vec3 c = mix(vec3(1,1,0), vec3(.7,cos(2.+time)*.5+.5,.4), .5+.5*dot(n,L));

    c = mix(c,vec3(.5,.1,.5),smoothstep(.3,.4,b*b*b));

	c = fract(pow(c*(1.+sin(.1*time))*500.,vec3(.75)));

    _ = vec4(mix(c,sqrt(c),cos(30.*time)),1);
}
