void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //vec2 expandUV = uv - 0.5f; --> The value .f failed to mobile device on Apple (GLSL ES)
	vec2 expandUV = uv - 0.5;
    vec2 polarUV = vec2(0,0);

    polarUV.y = sqrt(dot(expandUV, expandUV));
    polarUV.x = (atan(expandUV.x, expandUV.y)/3.14) * 0.5 + 0.5;

    // Time varying rotate expression
    float speed = iTime * 0.3;
    //polarUV.x= speed + polarUV.x;	
    //polarUV.y= speed + polarUV.y;
	polarUV.xy= speed + polarUV.xy;

    vec3 col = texture(iChannel0, polarUV).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
