void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = uv * 2. -1.;


    float y = cos(vec2(uv2.x + iTime/2., 0) * 3.14 * 2.).x / 2.;

    vec3 col;

    float delta = 1.- abs(uv2.y - y)/1.2;

    if (uv2.y > y){
    	col = vec3(1, 0, 0) * delta;
    }
    else{
    	col = vec3(0, 1, 0) * delta;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
