float blur = 8.0; // blur strength
float size = 1.5; // radius size factor
vec2 position  = vec2(0.5,.3);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    // can experiment with strength along x or y to fine tune look
	float focus = distance(uv.y,position.y); // vertical example
    //float focus = distance(uv.x,position.x); // horizontal example
    //float focus = distance(uv,position);// radial example
	float blur = blur*pow(focus,size);
	fragColor = texture(iChannel0, uv*1.0,blur);
}
