//디더링 할 줄 모르는 상태에서 시작, 대충 하다보니 되버림;;
//오후 11시 16분경 업데이트 : 전에 만든 픽셀화 쉐이더를 적용해서 요상하게 잘리는 것을 방지했다.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixel_size = 2.; //+ abs(cos(iTime))*10.;

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;

    vec2 pixel_scale = vec2(pixel_size);
    vec2 area = pixel_scale;//floor(iResolution.xy/pixel_scale);
    vec2 q = floor(fragCoord/area)*area/iResolution.xy;


    vec3 tex = texture(iChannel1,q).rgb;
    float num = texture(iChannel0,fragCoord/(8.*pixel_scale)).r*(1.-40./iResolution.x)+24./iResolution.x;
    float tex_avg = (tex.r + tex.g + tex.b)/3.;

    //col = floor(vec3(uv*0.5+normalize(vec2(0.5)-iMouse.xy/iResolution.xy)*0.5,1.0)/num); //테스트1
    //col = vec3(floor(uv.x/(num*iMouse.x/iResolution.x)),uv.x,1.0); //테스트2

    col = floor(vec3(tex_avg)/num);											//흑백
   	//col = floor(vec3(tex.r,0.7,0.3)/num);										//게임보이?
    //col = mix(tex,floor(vec3(tex.r,0.0,0.5)/num),0.75); 						//사이버 펑크 아저씨
	//col = floor(vec3(tex.r,tex.r,1.0)/num);									//블루라이트
    //col = floor(vec3(normalize(vec2(tex_avg)-tex.rg),1.0)/num);				//이건 모임?? 대충 오류 효과
    //col = floor(vec3(0.85,tex_avg,1.-tex_avg)/num);							//대충 예쁜 색
    //col = floor(tex/num);														//노멀?	
    //col = floor(vec3((tex_avg/8.+tex.r/(7./8.)),tex.g,tex.b)/num);			//개선 노멀?


    //col = tex;
	//col = clamp(vec3(1.0),vec3(0.0),col);
    fragColor = vec4(col,1.0);
}
