float nsin(float x)
{
    return .5+.5*sin(x);
}
float ncos(float x)
{
    return .5+.5*cos(x);
}
float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}  //https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9

highp float rand(vec2 co)  //from stackoverflow
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

uint hash( uint x ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}
float random( float f ) {  //http://amindforeverprogramming.blogspot.com/2013/07/random-floats-in-glsl-330.html

    const uint mantissaMask = 0x007FFFFFu;
    const uint one          = 0x3F800000u;

    uint h = hash( floatBitsToUint( f ) );
    h &= mantissaMask;
    h |= one;

    float  r2 = uintBitsToFloat( h );
    return r2 - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 0.0;
    float g = 0.0;
    float b = 0.0;
    vec2 xy0 = vec2(0.,0.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(fragCoord.xy-iResolution.xy)/iResolution.y;

    vec2 xy = vec2(0.,0.);
    float rr = .5*(nsin(.5*iTime))+3.45; // + .5*nsin(iTime*.5); //2.57 +nsin(iTime); //+cos(iTime);
    xy = uv;
    for (int i = 0; i<4; i++)
    {
        xy = rr*xy*(vec2(1.0,1.0)-xy);
        if(i==0)
        {
          xy0 = xy;  
        }
        if (i>0)
            {
                if(abs(length(xy) - length(xy0)) < .005 +.02*ncos(iTime*.15)) // try and tease out the cycles and color them
                {
                    r = 1.*random(float(mod(float(i),7.0)));
                    g = 1.*random(1.28+float(mod(float(i),7.0)));
                    b = 1.*random((1.0 - float(mod(float(i),7.0))));
                }
            }
    }
    if (length(xy) > .25+1.0*sin(iTime*.3))
    {
        r = 1.0 - mod(length(xy),2.);
        g = 0.2;
        b = mod(3.,length(xy));
    } else
    {
        //r = .05;
