void mainImage( out vec4 o, in vec2 f )
{
    vec2 uv = f / iResolution.xy + vec2(iTime * -.2, -.5),
        u = vec2(uv.x - uv.y * .6, uv.y);
    float r = 5., k = fract(u.x * r);


    if(uv.y < 0.){
        k = uv.y < -.83 / r ? 0. : min(fract(uv.x * r), k);  
    }
    o = mix(vec4(1), vec4(0,.1,.6,1), round(k));
    if(uv.y < 0.){
        o = mix(o, vec4(.9,.3,.1,1), round(fract(u.x * r + .5)));
    }
    if(fract(u.x*0.5) < 0.35){
         o = vec4(1.0);
    }
}
