void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    float d = 1.0 - (dot(uv, uv));
    d += iTime / 3.;
    d *= 3.;

    float id = floor(d);
    id = fract(id * 38.97);

    vec3 colors = id * vec3(46.35, 17.376, 49.84);
    colors = fract(colors);

    float m = fract(d);
    m = 2.0 * (m - 0.5);
    m = 1.0 - abs(m);
    m = smoothstep(0.0, 1.0, m);

    col += (colors * m);

    fragColor = vec4(col,1.0);
}
