void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = pow(texture(iChannel0, fragCoord / iResolution.xy), vec4(3.)) * 15.;
}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec4 sampleAdj(vec2 fragCoord, vec2 offset) {
    return texture(iChannel0, fract((fragCoord + offset) / iResolution.xy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame == 0) {
        fragColor = vec4(rand(fragCoord));
    } else {
        vec4 up = sampleAdj(fragCoord, vec2(0, 1));
        vec4 down = sampleAdj(fragCoord, vec2(0, -1));
        vec4 left = sampleAdj(fragCoord, vec2(-1, 0));
        vec4 right = sampleAdj(fragCoord, vec2(1, 0));

        vec4 neighbors = up + down + left + right;

        float frame = float(iFrame);

        // Noise
        vec4 pixel = vec4(rand(fragCoord + iTime),
                      rand(fragCoord - iTime),
                      rand(fragCoord * 2. + iTime), rand(fragCoord + 2. * iTime)) * 0.5 * 
            frame * frame * frame;

        // Convergence
        vec4 div = pixel / (neighbors / 4.);
        pixel -= div * 0.00940 * pow(frame, 0.4);

        // Automata
        if (length(neighbors) > 2.5) {
            pixel = vec4(0.);
        } else if (length(neighbors) < 1.) {
            pixel = vec4(0.);
        }

        // Motion
        float d = pow(frame, 0.9) / 10000.;
        if (pixel.x > 0.75) { pixel = mix(pixel, left, d); }
        else if (pixel.x < 0.25) { pixel = mix(pixel, right, d); }
        if (pixel.y < 0.25) { pixel = mix(pixel, up, d); }
        else if (pixel.y > 0.75) { pixel = mix(pixel, down, d); }

        pixel = pow(pixel, vec4(1.0000010139999999));


        fragColor = clamp(pixel, 0., 1.);
    }
}

