void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).xyz;

    // カラー加工用に逆ガンマ補正かける
    vec3 tmp = col;
	tmp = pow(tmp, vec3(2.2f));
    vec3 coef = vec3(0.2126f, 0.7152f, 0.0722f);

    float grayscale = tmp.x * coef.x + tmp.y * coef.y + tmp.z * coef.z;
	// ガンマ補正戻す
    grayscale = pow(grayscale, 1.0f/2.2f);

    // Time varying pixel color
//    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float rate = 0.5f + 0.5f * sin(iTime);
    vec3 color = mix(col, vec3(grayscale), rate);
    // Output to screen
    fragColor = vec4(color,1.0);
}
