void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const int max_iter = 60;
    float scale = 4.0;

    vec2 uv = scale * fragCoord/iResolution.xy - vec2(0.5 * scale);
    uv.y *= iResolution.y / iResolution.x;

    // c = 0.7885 * e ^ (i * alpha)
    float alpha = mix(0.0, 2.0 * 3.14159, fract(0.1 * iTime + 0.5));
    vec2 c = vec2(0.7885 * cos(alpha), 0.7885 * sin(alpha));


    // otros valores interesantes para c:
    // c = 0.285 + 0.01i
    // c = −0.70176 − 0.3842i
    // c = −0.8 + 0.156i
    // c = −0.7269 + 0.1889i

    vec2 Z = uv;

    int iter;
    for (iter = 0; iter < max_iter; ++iter)
    {
        vec2 Zn = Z;
        Zn.x = Z.x * Z.x - Z.y * Z.y;
        Zn.y = 2.0 * Z.x * Z.y;

        Z = Zn + c;

        if (dot(Z,Z) > 4.0)
        {
            break;
        }
    }

    float s = float(iter) / float(max_iter);    
    vec3 col = mix(vec3(0.0, 0.0, 1.0), vec3(1.0, 1.0, 1.0), 1.5 * s);

    // Output to screen
    fragColor = vec4(col,1.0);
}
