float myWidth = 4.0;
float myHeight = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.y / iResolution.x;


    uv.x *= myWidth;
    uv.y *= myHeight;

    uv.x /= aspect;

    vec4 col;

    float height = floor(mod(uv.y, 2.0));
    float width = floor(mod(uv.x, 2.0));


	if((floor(uv.y) < (myHeight) && floor(uv.x) < myWidth))
    {
        if(width < 0.5 && height < 0.5 || width > 0.5 && height > 0.5)
        {
            col = texture(iChannel0, uv);
        }

        else
        {
            col = texture(iChannel1, uv);
        }
    }



    // Output to screen
    fragColor = col;
}
