float dSegment( in vec2 p, vec2 a, vec2 b ) {
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0., 1. );
	return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y * (60. + 40. * sin(iTime * 0.25));

    vec2 nextDirection = vec2(1., 0.);
    float nextDirectionDot = 0.5 + 0.4 * dot(normalize(-uv), nextDirection);

    float distanceToNextPoint = length(uv);

    float targetSpeedMultiplier = (1. - (1. - max(min(distanceToNextPoint / 60., 1.), 0.)) * (1. - nextDirectionDot * nextDirectionDot));
	float result = 0.5 + 0.5*targetSpeedMultiplier;
    result = pow(result, 4.);
    vec3 col = vec3(result);

    if (dSegment(uv, vec2(0.), vec2(4., 0.)) < .25)
    	col = vec3(1., 0., 0.);

    fragColor = vec4(col,1.0);
}
