// Flower Wave by Joyer 2019
#ifdef GL_ES
precision highp float;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-st;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
	float boosted = iTime + 50.;
    float f = cos(a*boosted/10.) * 1./7.*sin(r*a*12.) + abs(1. - fract(boosted / 10.0) * 2.0);

    color = vec3((1.-smoothstep(f,f+(0.04*1./r),r)) * smoothstep(f-(0.05*1./r),f,r), 0.2, 0.3 );

    fragColor = vec4(color, 1.0);
}
