float map (vec3 p)
{
    vec3 q = fract(p) * 2.0 - 1.;

    float displacement = sin(cos(iTime * 0.25) * p.x) * sin(cos(iTime * 0.25) * p.y) * sin(cos(iTime * 0.25) * p.z) * 0.25;

    return length(q) + displacement - 0.25;
}


float trace (vec3 o, vec3 r)
{
    float t = 0.0;
    for (int i = 0; i < 32; ++i) {
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x/iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0));

    float the = iTime * 0.25;
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec3 o = vec3(0.0, iTime, iTime);

    float t = trace(o, r);

    float fog = 1.0 / (1.0 + t * t * 0.1);

    vec3 fc = vec3(fog);

    // Output to screen
    fragColor = vec4(fc,1.0);
}
