//	this show the optical flow from the current frame and the last one
//
//
//				IF THIS IS LAGGY ON THE IMAGE TAB CLICKS ON BUFFER A OR B
//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

	fragColor = texture(iChannel2, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(texture(iChannel0, uv));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //output the difference from the current frame and the last buffered frame
	vec3 color = (vec4(texture(iChannel1, uv)) - vec4(texture(iChannel0, uv))).rgb;

    vec3 col = texture(iChannel2, uv).rgb*0.95;
 	col += color;

    fragColor = vec4(col, 1.);
}
