void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy; 
    O = .1 + T(u); O.a = 0.;
    O = vec4( length(max(O,0.)) /3. );
    O += .2*T1(u);
 }

// --- previous frame

void mainImage( out vec4 O, vec2 u )
{ O = T(u); }

// #define gain   .5   // for LOD 99.
   #define gain   3.   // for LOD  4.

void mainImage( out vec4 O, vec2 U )
{
    vec2 I = floor( U / fTILE ) * fTILE,                // tile ID / tiling
         D = I +  fTILE/2.,                             // tile center
         V = mod(U, fTILE) - fTILE/2.;                  // local coords in tiles

//  vec4   m = textureLod(iChannel0,vec2(.5),99.),      // image average
//        m1 = textureLod(iChannel1,vec2(.5),99.);
    vec4   m = texelFetch(iChannel0, ivec2(D  )>>4,4),  // image average. 4 = log2(TILE)
          m1 = texelFetch(iChannel1, ivec2(D+V)>>4,4), d;
    float  s = gain* sqrt(max(0.,  m.a - dot3(m ))),    // image variance Ex²-E²x
          s1 = gain* sqrt(max(0., m1.a - dot3(m1)));   
    O-=O;
    for (int k=0; k < TILE*TILE; k++)                   // correlation between current central tile
        D = I + vec2(k%TILE,k/TILE),                    // and previous frame areas around
        O += ( T(D) - m ) / s  * ( T1(D+V) - m1 ) / s1; // coorrelation centered and normalized
     // d  = ( T(D) - m ) / s  - ( T1(D+V) - m1 ) / s1 , O += d*d; // variante: distance rather than correl
                                                        // indeed, same thing: dist = 2*(1 - correl )
     // O += ( T(D) - T(D+V) ) / s  * ( T1(D) - T1(D+V) ) / s1; // trying to cancel spatial correlation
     // d  = ( T(D) - T(D+V) ) / s  - ( T1(D) - T1(D+V) ) / s1, O += 1e15*d*d; // variante: distance rather than correl
    O /= fTILE*fTILE;
 }

// --- current frame

void mainImage( out vec4 O, vec2 u )
{ O = T(u); O.a = dot3(O); }
