float map(vec3 p) {
    vec3 q = fract(p) * 2.0 - 1.0;
 	return length(q) - 0.40;
}

float trace(vec3 origin, vec3 ray) {
    float t = 0.0;
    for (int i = 0; i < 20; ++i) {
     	vec3 p = origin + ray * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // Transform Coordinate to -1 to 1
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0));
    vec3 o = vec3(0.0, 0.0, iTime);

    float the = iTime * .1;
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float fog = 1.0 / (1.0 + 0.1 * t * t);

    vec3 fc = vec3(fog, 0.5 + 0.7 * sin(iTime * .25), 0.7 + 0.5 * sin(iTime * .25));

    fragColor = vec4(fc, 1.0);
}
