void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float k=iResolution.x/4.0;
    vec2 xy = fragCoord.xy; //Получаем координаты текущего пикселя
    vec2 cxy0 = vec2((xy.x-iResolution.x/2.0)/k,(xy.y-iResolution.x/3.5)/k);
    vec2 cxy = cxy0;
    vec2 cxy1 = cxy;
    for (int i=0;i<3000;i++)
    {
       cxy[0]=cxy1[0]*cxy1[0]-cxy1[1]*cxy1[1]+cxy0[0]; 
       cxy[1]=2.0*cxy1[0]*cxy1[1]+cxy0[1];
       cxy1 = cxy;
    }
    float b=cxy1[0]*cxy1[0]+cxy1[1]*cxy1[1];
    if (b<2.0)
    fragColor = vec4(255,255,255,1.0);
    else
    fragColor = vec4(0,0,0.0,1.0);
}
