// Voronoi and fractal noise functions from https://www.shadertoy.com/view/MslGD8

float Hash(in vec2 p)
{
	float h = dot(p, vec2(12.9898, 78.233));
    return -1.0 + 2.0 * fract(sin(h) * 43758.5453);
}

vec2 Hash2D(in vec2 p)
{
	float h = dot(p, vec2(12.9898, 78.233));
    float h2 = dot(p, vec2(37.271, 377.632));
    return -1.0 + 2.0 * vec2(fract(sin(h) * 43758.5453), fract(sin(h2) * 43758.5453));
}

float Noise(in vec2 p)
{
    vec2 n = floor(p);
    vec2 f = fract(p);
	vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(mix(Hash(n), Hash(n + vec2(1.0, 0.0)), u.x),
               mix(Hash(n + vec2(0.0, 1.0)), Hash(n + vec2(1.0)), u.x), u.y);
}

float FractalNoise(in vec2 p)
{
    p *= 5.0;
    mat2 m = mat2(1.6,  1.2, -1.2,  1.6);
	float f = 0.5000 * Noise(p); p = m * p;
	f += 0.2500 * Noise(p); p = m * p;
	f += 0.1250 * Noise(p); p = m * p;
	f += 0.0625 * Noise(p); p = m * p;

    return f;
}

vec3 Voronoi(in vec2 p)
{
    vec2 n = floor(p);
    vec2 f = fract(p);

	vec2 mg, mr;

    float md = 8.0;
    for(int j = -1; j <= 1; ++j)
    {
        for(int i = -1; i <= 1; ++i)
        {
            vec2 g = vec2(float(i), float(j));
            vec2 o = Hash2D(n + g);

            vec2 r = g + o - f;
            float d = dot(r, r);

            if(d < md)
            {
                md = d;
                mr = r;
                mg = g;
            }
        }
    }
	return vec3(md, mr);
}

vec3 ApplyFog(in vec2 texCoord)
{
    vec3 finalColor = vec3(0.0);    

    vec2 samplePosition = (4.0 * texCoord.xy / iResolution.xy) + vec2(0.0, iTime * 0.0025);
    float fogAmount = FractalNoise(samplePosition) * 0.175;

    vec3 fogColor = vec3(texCoord.xy / iResolution.xy + vec2(0.5, 0.0), sin(iTime) * 0.25 + 0.5);
    finalColor = fogColor * fogAmount * vec3(sin(iTime) * 0.00125 + 0.75);  

    return finalColor;
}

vec3 AddStarField(vec2 samplePosition, vec3 color, float threshold)
{
    vec3 starValue = Voronoi(samplePosition);
    if(starValue.x < threshold)
    {
        float power = 1.0 - (starValue.x / threshold);

        return vec3(color*power);
    }
    //return vec3(0.5);
   	return vec3(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float maxResolution = max(iResolution.x, iResolution.y);

	//vec3 finalColor = ApplyFog(fragCoord.xy);
    vec3 finalColor = vec3(0.25, 0.03, 0.4);


    // Add Star Fields
    vec2 samplePosition = (fragCoord.xy / maxResolution) + vec2(0.0, iTime * 0.01);    
    finalColor += AddStarField(samplePosition * 16.0, vec3(1,0,0), 0.003); // color 1 (red)

    samplePosition = (fragCoord.xy / maxResolution) + vec2(0.0, iTime * 0.004);    
    finalColor += AddStarField(samplePosition * 20.0, vec3(0,1,0), 0.0025); // color2 (green)

    samplePosition = (fragCoord.xy / maxResolution) + vec2(0.0, iTime * 0.0005 + 0.5);    
    finalColor += AddStarField(samplePosition * 8.0, vec3(0,0,1), 0.0007); // color3 (blue)

    fragColor = vec4(finalColor, 1.0);
}
