#define PI acos(-1.)
#define TAU PI*2.
#define hue(t) (cos((vec3(0,2,-2)/3.+t)*TAU)*.5+.5)

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 v=cross(a,p),u=cross(v,a);
	p=u*cos(t)+v*sin(t)+a*dot(p,a);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=vec4(0);
	vec2 p=(fragCoord*2.-iResolution.xy)/iResolution.y;
    p=vec2(atan(p.y,p.x)/PI,length(p));
    for(float i=0.;i<10.;i++)
    {
        vec3 q=vec3(p.x,1.5/p.y-i-fract(-iTime),i);
        for(int j=0;j<6;j++)
        {
            q.xy=abs(q.xy)-.5;
            q.xz=abs(q.xz)-.2;
            rot(q,vec3(2,-3,8),iTime*.5+cos(iTime*.2)*.5);
        }
		fragColor.xyz+=mix(vec3(1),hue(i*.2+iTime),.8)*.006/abs(q.y);
    }
}
