#define TURBULENCE 5.0
#define NOISE_SCALEX 5.0
#define NOISE_SCALEY 7.0
#define RING_RADIUS 4.0
#define INTENSITY 0.5
#define BACKGROUND_COLOR vec3(0.2, 0.1, 0.1)
#define BASE_COLOR vec3(0.1, 0.9, 0.9)
#define BASE_COLOR1 vec3(0.3, 0.3, 0.6)
#define TIME_SCALE 0.2

float noise(in vec2 p)
{
    return texture(iChannel0, p/255.0).r;
}

mat2 m2 = mat2(0.8, 0.6, -0.6, 0.8);
float fbm(in vec2 p)
{
    float f = 0.0;
    f += 0.5000*noise( p ); p = m2*p*2.02;
    f += 0.2500*noise( p ); p = m2*p*2.03;
    f += 0.1250*noise( p ); p = m2*p*2.01;
    f += 0.0625*noise( p );
    f /= 0.9375;
    return f;
}

float warp(in vec2 p)
{
    float t = iTime * TIME_SCALE;
    vec2 q = vec2( fbm( p * NOISE_SCALEX ),
                   fbm( p * NOISE_SCALEY ) );

    float d = RING_RADIUS*length(p);
    float n = fbm( TURBULENCE*q + d - t);
    return n;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/iResolution.xy;  
    vec3 baseCol = BASE_COLOR;
    vec3 baseCol1 = BASE_COLOR1;
    vec3 bgCol = BACKGROUND_COLOR;
    vec3 col =  vec3(0.0);
    vec2 mouse = (2.0*iMouse.xy - iResolution.xy)/iResolution.xy;
    uv -= vec2(mouse);

    float f =  warp(uv);
    float f3 = smoothstep(0.8-INTENSITY, 1.0,f);
    vec3 pixCol = mix(baseCol,baseCol1, 0.5 + 0.5*sin(iTime*0.5 + dot(uv, vec2(0.1, 0.5))));
    col = mix(bgCol, pixCol, f3);

    fragColor = vec4(col,1.0);
}
