const V1 pi16 = 6.283 / 16.;

V2	P;
V0 bkg( ) {

    V2	s = V2( lessThan( _.ww, N ) );
    V1	n = s.x + 2. * s.y;

    O = .25 * tx( n, mod( 2. * N, _.yy ) );
}

BO hit_rect( V3 eye, V3 ray, V3 pos, V3 x, V3 y, V3 n, V1 id, inout V1 idbest, inout V3 abl, inout V3 hit  ) {

    V3	d = pos - eye;

    V1	l = dot( d, n ) / dot( ray, n );

    if( 0. < l && l < abl.z ) {

        V3	h = eye + l * ray,
            c = h - pos;

        V1	cx = dot( c, x ),
            xx = dot( x, x ),
            cy = dot( c, y ),
            yy = dot( y, y );

        if( abs( cx ) < xx && abs( cy ) < yy ) {

            idbest = id;

            abl = V3( cx / xx, cy / yy, l );

            hit = h;

            RT
        }
    }

    RF
}


BO hit_light( V3 eye, V3 ray, V3 pos, V1 rad, V1 id, inout V1 idbest, inout V1 len, inout V3 hit ) {

    V3	d  = eye - pos;
    V1	ab = dot( ray, d ),
        bb = dot( d, d ),
        ds = rad * rad - bb + ab * ab;

    if( 0. <= ds ) {

        V1	l = -ab - sqrt( ds );

        if( 0. < l && l < len ) {

            idbest = id;

            len = l;

            hit = eye + l * ray;

            RT
		}
	}

    RF
}


V1 hit_obj( V3 eye, V3 ray, V1 nid, out V3 hit, out V3 n, out V3 abl ) {

    V1	id     = -1.,
        idbest = -1.,
    	beta,
        c,
        s;

    V3	pos, x, y, z;

    abl = V3( 0, 0, 1.e3 );

    //plane
    if( nid != ID_PLANE ) {

        id = ID_PLANE;

        pos = rd( id, 0. );
        x   = rd( id, 1. );
        y   = rd( id, 2. );
        z   = rd( id, 3. );

        hit_rect( eye, ray, pos, x, y, z, id, idbest, abl, hit );
    }

    id = ID_MIN_PHOTO + 0.;

    while( id <= ID_MAX_PHOTO ) {

        if( nid != id ) { 

        	pos = rd( id, 0. );
        	x   = rd( id, 1. );
        	y   = rd( id, 2. );
            z	= rd( id, 3. );

			hit_rect( eye, ray, pos, x, y, z, id, idbest, abl, hit );
        }

        ++ id;
    }

    //light
	id = ID_LIGHT;

    if( id != nid ) {

        pos = rd( id, 0. );

		hit_light( eye, ray, pos, .5, id, idbest, abl.z, hit );
    }

    RN idbest;
}


V0 mainImage( out V4 o, V2 i ) {

    INIT

    P = ( 2. * N - 1. ) * V2( iR.x * Ri.y, 1. ); 

    //bkg( );

    V1	g = .25 * 3.142 * ( iMouse.y * Ri.y - 1. );

	V3	eye = rot( V3( 0, 0., 25. ), V3( 1, 0, 0 ), g ),
        scn = rot( V3( P, 22. ), V3( 1, 0, 0 ), g ),
        ray = normalize( scn - eye ),
        light = rd( ID_LIGHT, 0. ),
        hit, n, abl;

    V1	id = hit_obj( eye, ray, -1., hit, n, abl );

    if( id == ID_LIGHT ) {

        O.xyz = V3( 1. );
    }

    if( ID_PLANE == id ) {

        V3	n    = rd( ID_PLANE, 3. ),
            _ray = light - hit,
            _abl = V3( 0., 0., 1.e3 ),
            _hit,
            _n,
            _c = V3( .25 ) + .75 * tx( 2., V2( .75, .75 ) + .25 * abl.xy ).rgb;

        O.rgb = _c;

        V1	d =
            ID_LIGHT == hit_obj( hit, normalize( _ray ), ID_PLANE, _hit, _n, _abl )
            	? clamp( 2. * dot( n, _ray ) * pow( dot( _ray, _ray ), -1. ), 0., 1. )
            	: 0.;

        O.rgb *= clamp( .25 + .75 * d, 0., 1. );

        _abl.z = 1.e3;

        V3	v = reflect( ray, n );

        V1  w = hit_obj( hit, normalize( v ), ID_PLANE, _hit, _n, _abl );        

        if( ID_MIN_PHOTO <= w && w <= ID_MAX_PHOTO ) {

            V1 cl = mod( w, 4. );

            V2 off = .25 + .5 * V2( mod( cl, 2. ), mod( floor( cl / 2. ), 2. ) );

            V2 j = off + V2( .25 ) * _abl.xy;

            if( dot( v, rd( w, 3. ) ) < 0. ) {

                O.rgb = .1 * tx( mod( w / 4., 4. ), j ).rgb + .9 * O.rgb;
            }
            else {

                O.rgb = .9 * O.rgb;
            }
        }

        if( w == ID_LIGHT ) {

            O.rgb = .75 * O.rgb + V3( .25 );
        }
    }

    if( ID_MIN_PHOTO <= id && id <= ID_MAX_PHOTO ) {

        V3	n    = rd( id, 3. ),
            _ray = light - hit,
            _abl = V3( 0., 0., 1.e3 ),
            _hit,
            _n;

        V1 cl = mod( id, 4. );

        V2 off = .25 + .5 * V2( mod( cl, 2. ), mod( floor( cl / 2. ), 2. ) );

        V2 j = off + V2( .25 ) * abl.xy;

        if( dot( ray, n ) < 0. ) {

            O.rgb = tx( mod( id / 4., 4. ), j ).rgb;
        }
        else {

            n = -n;

            O.rgb = V3( .75 );
        }

    	V1	d =
            ID_LIGHT == hit_obj( hit, normalize( _ray ), id, _hit, _n, _abl )
            	? clamp( 2. * dot( n, _ray ) * pow( dot( _ray, _ray ), -.5 ), 0., 1. )
            	: 0.;

		O.rgb *= clamp( .25 + .75 * d, 0., 1. );

    }

    O.rgb +=  pow( 1000., - acos( dot( ray, normalize( light - eye ) ) ) );
    O.rgb = clamp( O.rgb, 0., 1. );


    FIN
}

const V1 pi16 = 6.283 / 16.;
V1	alpha;


V0 mainImage( out V4 o, V2 i ) {

    INIT

    if(  i.y < 4. ) {

	    alpha = -4. * 6.283 * ( .5 - iMouse.x * Ri.x + .01 * iT );

        V1	id = floor( i.x ),
            beta = id * pi16,
            gamma = .1 * iT,
            c = cos( alpha + beta ),
            s = sin( alpha + beta );

        if( i.x < 16. ) {

            O.xyz = 
                i.y < 2.
                	? i.y < 1.
                		? V3( 7. * s, 1., 7. * c ) //pos
                		: V3( c, 0, -s ) //x
                	: i.y < 3.
                        ? V3( 0, 1, 0 ) // y
                        : V3( s, 0., c ); // n
        }
        else {

            if( i.x < 17. ) { // plane

                O.xyz = 
                    i.y < 2.
                    	? i.y < 1.
                    		? V3(  0, 0,  0 )
                    		: 10. * V3( s, 0, c )
                    	: i.y < 3.
                            ? 10. * V3( c, 0,  -s )
                            : V3(  0, 1,  0 );
            }
            else {

                O.xyz = i.x < 18. ? M3(s,0,c,0,1,0,c,0,-s)*rot( V3( 0, 4.2 - 2. * sin( gamma ), 13.1 * cos( gamma ) ), V3(0,1,0), .51 * cos( 10.*gamma ) ) : V3( 0 ); // light
            }
        }
    }
    else {

        V2 s = V2( lessThan( _.ww, N ) );
        V1 n = s.x + 2. * s.y;
        N = mod( 2. * N, _.yy );
        O = tx( n, N );
    }  

	FIN 
}
