float N21(vec2 uv){
	return fract(
        sin(uv.x*1377.425)+sin(uv.y*2645.5623) *
        sin(uv.x*1247.78977)+sin(uv.y*16658.58273)
    );
}
vec2 N22(vec2 uv){
	return vec2(N21(uv),N21(uv+N21(uv)));
}
vec3 N23(vec2 uv){
	return vec3(N21(uv),N21(uv+N21(uv)),N21(uv+N21(uv+N21(uv))));
}

float pattern(vec2 uv,float time){
    vec2 lvid = floor(uv);
    vec2 lv = fract(uv);
    lv -= 0.5;


    float col = 0.;
    float rad = 0.1;

    float closest = 2.;
    float next_closest = 2.;
    for(int x=-1;x<2;x++){
        for(int y=-1;y<2;y++){
            vec2 cid = lvid+vec2(x,y);
            float influence = sin(2.*time*N21(cid))*.2+1.;
            //influence = 1.;
            vec2 offset = sin(5.*time*(N22(cid)*.1+.1))*.2;
            float dist = length(lv+offset-vec2(x,y))*influence;
            if(dist<closest){
                next_closest = closest;
                float delta = abs(closest-dist);
                closest = dist;


            }
            else if(dist<next_closest){
             	next_closest = dist;   
            }
            col = max(
                .0*smoothstep(.1,.09,abs(next_closest-closest)),
                1.0*smoothstep(1.0,.10,next_closest)
            );
        }
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float t = -iTime * .05;

    uv *= mat2(sin(t),cos(t),-cos(t),sin(t));

    uv *= 10.;

    vec3 col = vec3(0.);
    float tm = iTime*1.;
    for(float i=1.;i<8.;i++){
        vec3 ccol = vec3(1.,.25,1.)*(sin(vec3(tm+i*5.,tm*1.2+i*7.,tm+1.3+i*11.))*.4+.9 + N23(uv)*.2);
    	col += ccol * pattern(uv/(i),iTime+i*1000.)*.5;
    }

    fragColor = vec4(col,1.0);
}
