void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy) /iResolution.y;
    vec4 m = iMouse / iResolution.x;
    uv *= 16.;

    float t=mod(iTime *.4,2.)-1.;

    float c = sin(uv.x) * cos(uv.y);  //Basis of effect

	vec3 col = vec3(0.0);

	if( m.z>0.0 ) {
        c= smoothstep(0.,16./iResolution.y, c - (m.x -.5)*2. );
	} 
    else  {
        c= smoothstep(0.,16./iResolution.y, c - t );
    }
    fragColor = vec4(c,c*.6,c*.3,1.);
}
