void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

	for(float i=1.;i<9.;i+=1.)
	{
       p+= 0.5/i * sqrt(abs(cos(i*p.yx+0.3*iTime+vec2(.03*i,.03*i))));
	}
	fragColor=vec4(1.5*fract(1.5*p.x)-0.5,1.5*fract(1.5*p.y)-0.5,0.0,1.0);
}
