#define PI 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord /= iResolution.xy;

    vec2 Offset;
    Offset.x = cos(2.0 * PI * (iTime / 10.0 + fragCoord.y));
    Offset.y = sin(2.0 * PI * (iTime / 10.0 + fragCoord.x));
    fragCoord += Offset / 10.0;

    fragColor = texture(iChannel0, fragCoord);
}
