void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Pixel Coordinates
    vec2 uv = fragCoord/iResolution.xy;



    //simple way for grayscale
    //green is the lightest color out of rgb. 
    //grayscale this simplified is not the most accurate
    fragColor = vec4(mix(texture(iChannel0,uv).gggg,texture(iChannel0,uv).rrrr,texture(iChannel0,uv).bbbb));
}
