vec3 nobles[120];
const float PI = 3.14159265359;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dir;
    for(int i = 0; i < nobles.length(); i++){
    	nobles[i] = vec3(
        	fract(pow(float(i),1.2)*0.323 + sin(iTime*sin(float(i))*2.0)*0.05),
            fract(pow(float(i),1.3)*0.456 + cos(iTime*sin(float(i))*2.0)*0.05),
            fract(float(i) * 2123.2345)
        );

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, 0.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, 0.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, 0.0)),2.0);

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, 1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, 1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, 1.0)),2.0);

        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(0.0, -1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(1.0, -1.0)),2.0);
        dir += vec2(cos(nobles[i].z), sin(nobles[i].z)) / pow(distance(uv, nobles[i].xy + vec2(-1.0, -1.0)),2.0);

    }

    vec3 col;

    if(atan(dir.y, dir.x) < 0.45) col = vec3(1, 0.105, 0.551);
    else if(atan(dir.y, dir.x) < 0.55)  col = vec3(1.0, 0.852, 0.0);
    else col = vec3(0.105, 0.699, 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
