void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 baseUV = fragCoord/iResolution.xy;
    int octaves = 3;
    vec3 color = vec3(0);
    float strength = 1.0;
    float uvSpeed = 1.0;
    for (int i = 0; i < octaves; i++) {
        vec2 hej = baseUV*uvSpeed;
        hej.y += iTime * 0.5;
        color += (texture(iChannel0, hej).rgb)*strength/3.0;
        strength *= 2.0;
        uvSpeed *= 2.0;
    }
    // Output to screen
    fragColor = vec4(color,1.0);
}
