float radius = .5;
float timeScale = 2.;
float pi = 3.14;

float pulsing(){
    float fi = fract(iTime)*2.*pi;
	return (1. - cos(fi));
}

float fractured(float offset){
    float _fractured = fract(iTime*timeScale + offset);
    _fractured = distance(_fractured, 0.5) * 2.;
    _fractured = 1. - _fractured;
    return _fractured;
}

vec3 nowCol(){
	return vec3(
        fractured(0.),
        fractured(0.33),
        fractured(0.67)        	
    );    
}


vec4 circle(vec2 center, vec2 pos, float radius){
    float dec = 15. * pulsing() + 5.;
    float mult = 1. - distance(
                          sqrt(
                           pow((pos.x - center.x), 2.) + pow((pos.y - center.y), 2.)), radius) / dec;

   return vec4(nowCol()*mult, 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 xy = fragCoord;

  	vec2 center = iResolution.xy * 0.5;
    radius = iResolution.y * 0.5 * radius;
    fragColor = circle(center, xy, radius) * circle(center, xy, radius*1.5);
    //fragColor = circle(center, xy, radius);
}
