#define _Speed 0.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //resolve uv of pixel
	vec2 uv = fragCoord.xy / iResolution.xy;


    // get scroll value
     vec2 scroll = vec2(fract(iTime*_Speed), fract(iTime*_Speed));

    // sample texture
    vec4 col = texture(iChannel0, uv - scroll);



    //output final color
    fragColor = col;
}
