void mainImage( out vec4 O, vec2 U )
{
    U -= .3;                      // makes U uneasy for windows
 // U -= .5;                      // makes U integer
 // O = vec4( sqrt(U*U) == U );   // white if sqrt(x²)==x
    O = vec4( sqrt( U * min(U,iTime+1e11) ) == U );  // counters the optimizer
}
