// https://www.shadertoy.com/view/4djSRW use this version please!

#define PI 3.14159265359

// http://byteblacksmith.com/improvements-to-the-canonical-one-liner-glsl-rand-for-opengl-es-2-0/
highp float rand( const in vec2 uv, float seed ) {
    const highp float a = 12.9898, b = 78.233, c = 43758.5453;
    highp float dt = dot( uv.xy + seed, vec2( a, b ) ), sn = mod( dt, PI );
    return fract(sin(sn) * c + seed);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = .5 + .5 * cos(iTime + uv.xyx + vec3(0, 2, 4)) + rand(uv * iTime, 0.);

    fragColor = vec4(col, 1.);
}
