#define R(a)a=vec2(a.x+a.y,a.x-a.y)*.7
void mainImage(out vec4 O, vec2 C) {
    O=vec4(0);
	for(float g,e,j,i;i++<50.;){
   		vec3 p=g*vec3((C*2.-iResolution.xy)/iResolution.y,1);
        p.z+=iTime;
        p=fract(p)-.5;
        p.xz=abs(p.xz)-.15-.05*(sin(iTime*2.));
        R(p.xz);R(p.yz);
        p.yz=abs(p.yz)-.1;
        R(p.yz);
        g+=e=.5*length(p.xy)-.01;
        e<.01?O.xyz+=.4/i:p;
    }
}
