void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float c = 1.45;
    float t = iTime * c;
    float s = t * 128.0; //sin(t/1.5) * 150.0 + 50000.0;

    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5 * cos(t + uv.xyx + vec3(0,2,4));

    vec2 center = vec2(iResolution.x / 2.0, iResolution.y/2.0);
    float x = fragCoord.x - center.x;
    float y = fragCoord.y - center.y;
    float xSquared = x * x;
    float ySquared = y * y;

    fragColor = vec4(col,1.0);
    fragColor.g *= 0.5;
    fragColor.g += sin(t * 12.0 * (xSquared + ySquared)/s - 2.001 * s);
    fragColor.b += sin(t * 12.0 * (xSquared + ySquared)/s - 2.0011 * s)/50.0;
    fragColor.r += sin(t * 12.0 * (xSquared + ySquared)/s - 2.0012 * s)/100.0;

}
