void mainImage(out vec4 O, vec2 T) {
    vec2 R=iResolution.xy,U=(T-.5*R.xy)/R.y;
    float D,t=iTime;
    vec3 P,B = normalize( vec3( U.x, U.y,1) );
    for(int i = 0; i<64;i++) {
        P = vec3(0, sin(U.x*5.+t), -5)+ B*D;
        P.z-=t*6.; 
        P=mod(P,3.)-1.5;
        P.z -= clamp( P.z, 0., 5. );
        D+=length(P) - .05;
    }
    O.r=O.g=((D< 16.) ? 1.:0.);
}
