void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float timer=mod(iTime, 3.0);
    float radius=2.0/timer; // divided by time so that it changes as time progresses
    float width=timer*3.0;
    float ring=length(vec2(uv.x+0.2, uv.y))*radius*width-width;
    ring=min(2.0, abs(1.0/(10.0*ring)));
    ring=max(0.0, ring-timer); // these are used to reset the pulse effect after a certain amount of time
    vec3 rings=vec3(0.5, .09, 0.08);

    timer=mod(iTime*1.2+2.0, 3.0);
    radius=1.3/timer;
    width=2.0*timer;
    ring=length(vec2(uv.x-1.0, uv.y+0.2))*radius*width-width; 
    rings+=vec3(ring, ring*.4, ring*.5);


	fragColor = vec4(rings,4.0);
}
