float numRows = 9.0;
float numCol = 9.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 printOut = vec4(0.0, 0.0, 0.0, 1.0);

    //sets background texture
    vec4 color = texture(iChannel2, uv);
    color.w = 1.0;

    //splits x axis into numRows equal parts
    //splits y axis into numCol equal parts
    //assigns a value of 0 or 1 to each section
    float col = mod(floor(uv.x * numCol), 2.0);
    float row = mod(floor(uv.y * numRows), 2.0);

    //compares value of each section
    //if equal set color to texture0
    if (row == col)
    {
        color = texture(iChannel0, uv);
    }
    //otherwise sets color to an average of texture0 and texture1
    else
    {
        color = texture(iChannel1, uv);
    }    

    printOut = mix(printOut, color, color.a);
    fragColor = printOut;

